mapper.select:
SELECT *
FROM employee
WHERE employee_no =
#{employee_no}
AND
busyo_dai_code = #{busyo_dai_code}
AND busyo_cyu_code =
#{busyo_cyu_code}

mapper.update:
UPDATE employee
SET

	employee_no = #{employee_no}



	employee_no = #{employee_no}


	,busyo_dai_code = #{busyo_dai_code}


	,busyo_cyu_code = #{busyo_cyu_code}


	,busyo_syou_code = #{busyo_syou_code}


	,busyo_name = #{busyo_name}


	,position_code = #{position_code}


	,employee_name = #{employee_name}


	,type_code = #{type_code}


	,gender = #{gender}


	,birthday = #{birthday}


	,postal_code = #{postal_code}


	,address_1 = #{address_1}


	,address_2 = #{address_2}


	,employee_memo = #{employee_memo}

WHERE employee_no = #{employee_no}
AND busyo_dai_code =
#{busyo_dai_code}
AND busyo_cyu_code = #{busyo_cyu_code}

mapper.delete:
DELETE FROM employee
WHERE employee_no =
#{employee_no}
AND busyo_dai_code =
#{busyo_dai_code}
AND busyo_cyu_code
= #{busyo_cyu_code}
