mapper.update:
UPDATE position
SET

	position_code = #{position_code}

 , 

	position_code = #{position_code}


	,position_name = #{position_name}


	,position_name_small = #{position_name_small}


	,position_start = #{position_start}


	,position_end = #{position_end}

WHERE position_code = #{position_code}
