mapper.select:
SELECT *
FROM rank_syou
WHERE rank_code = #{rank_code}
AND
busyo_start = #{busyo_start}

mapper.update:
UPDATE rank_syou
SET

	rank_code = #{rank_code}

 , 

	rank_code = #{rank_code}


	,busyo_name = #{busyo_name}


	,busyo_name_small = #{busyo_name_small}


	,busyo_start = #{busyo_start}


	,busyo_end = #{busyo_end}

WHERE rank_code = #{rank_code}
AND busyo_start = #{busyo_start}

mapper.delete:
DELETE FROM rank_syou
WHERE
rank_code = #{rank_code}
AND busyo_start = #{busyo_start}
